"use client";

import Image from "next/image";

const AnimatedEmoji = () => {
  return (
    <div className="flex h-full  w-full  items-center justify-center">
      <Image
        alt="Animated Emoji"
        height={150}
        src="https://raw.githubusercontent.com/Tarikul-Islam-Anik/Animated-Fluent-Emojis/master/Emojis/Smilies/Smiling%20Cat%20with%20Heart-Eyes.png"
        width={150}
      />
    </div>
  );
};

export default AnimatedEmoji;

import * as React from "react";

import { cn } from "@/lib/utils";

const Card = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => (
  <div
    ref={ref}
    className={cn(
      "rounded-xl border bg-card text-card-foreground shadow",
      className,
    )}
    {...props}
  />
));
Card.displayName = "Card";

const CardHeader = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => (
  <div
    ref={ref}
    className={cn("flex flex-col space-y-1.5 p-6", className)}
    {...props}
  />
));
CardHeader.displayName = "CardHeader";

const CardTitle = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLHeadingElement>
>(({ className, ...props }, ref) => (
  <h3
    ref={ref}
    className={cn("font-semibold leading-none tracking-tight", className)}
    {...props}
  />
));
CardTitle.displayName = "CardTitle";

const CardDescription = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement>
>(({ className, ...props }, ref) => (
  <p
    ref={ref}
    className={cn("text-sm text-muted-foreground", className)}
    {...props}
  />
));
CardDescription.displayName = "CardDescription";

const CardContent = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => (
  <div ref={ref} className={cn("p-6 pt-0", className)} {...props} />
));
CardContent.displayName = "CardContent";

const CardFooter = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => (
  <div
    ref={ref}
    className={cn(" flex items-center p-6 pt-0", className)}
    {...props}
  />
));
CardFooter.displayName = "CardFooter";

export {
  Card,
  CardHeader,
  CardFooter,
  CardTitle,
  CardDescription,
  CardContent,
};

"use client";
import { motion, useInView } from "framer-motion";
import { useRef } from "react";

// Fungsi untuk mengekspor semua animasi hooks
function useCardIdentityAnimation() {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, amount: 0.3 });

  return {
    ref,
    variants: {
      hidden: { opacity: 0, x: -100 },
      visible: {
        opacity: [0, 0.2, 0.8, 1],
        x: 0,
        transition: { duration: 0.5, delay: 0.1 },
      },
    },
    initial: "hidden",
    animate: isInView ? "visible" : "hidden",
  };
}

function useVerticalCardAnimation() {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, amount: 0.3 });

  return {
    ref,
    variants: {
      hidden: { opacity: 0, y: -100 },
      visible: {
        opacity: [0, 0.2, 0.8, 1],
        y: 0,
        transition: { duration: 0.5, delay: 0.2 },
      },
    },
    initial: "hidden",
    animate: isInView ? "visible" : "hidden",
  };
}

function useQuoteCardAnimation() {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, amount: 0.3 });

  return {
    ref,
    variants: {
      hidden: { opacity: 0, x: 100 },
      visible: {
        opacity: [0, 0.2, 0.8, 1],
        x: 0,
        transition: { duration: 0.5, delay: 0.3 },
      },
    },
    initial: "hidden",
    animate: isInView ? "visible" : "hidden",
  };
}

function useCardSloganAnimation() {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, amount: 0.3 });

  return {
    ref,
    variants: {
      hidden: { opacity: 0, x: -100 },
      visible: {
        opacity: [0, 0.2, 0.8, 1],
        x: 0,
        transition: { duration: 0.5, delay: 0.5 },
      },
    },
    initial: "hidden",
    animate: isInView ? "visible" : "hidden",
  };
}

function useCardProductAnimation() {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, amount: 0.3 });

  return {
    ref,
    variants: {
      hidden: { opacity: 0, x: 100 },
      visible: {
        opacity: [0, 0.2, 0.8, 1],
        x: 0,
        transition: { duration: 0.5, delay: 0.5 },
      },
    },
    initial: "hidden",
    animate: isInView ? "visible" : "hidden",
  };
}

export const useMobileAnimation = () => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, amount: 0.3 });

  return {
    ref,
    variants: {
      hidden: { opacity: 0 },
      visible: {
        opacity: 1,
        transition: { duration: 0.5, delay: 0.2 },
      },
    },
    initial: "hidden",
    animate: isInView ? "visible" : "hidden",
  };
};

export const useAnimations = () => ({
  cardIdentity: useCardIdentityAnimation(),
  cardSlogan: useCardSloganAnimation(),
  cardProduct: useCardProductAnimation(),
  verticalCard: useVerticalCardAnimation(),
  quoteCard: useQuoteCardAnimation(),
});
